import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
  IconButton,
  Tooltip,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
} from '@mui/material';
import {
  Add as AddIcon,
  Description as DescriptionIcon,
  Comment as CommentIcon,
  AttachFile as AttachmentIcon,
} from '@mui/icons-material';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

const Claims = () => {
  const [claims, setClaims] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedClaim, setSelectedClaim] = useState(null);
  const [newClaimDialogOpen, setNewClaimDialogOpen] = useState(false);
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [commentDialogOpen, setCommentDialogOpen] = useState(false);
  const [comment, setComment] = useState('');

  useEffect(() => {
    fetchClaims();
  }, []);

  const fetchClaims = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/claims/user');
      setClaims(response.data);
    } catch (err) {
      setError('Failed to fetch claims');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const formik = useFormik({
    initialValues: {
      policyId: '',
      type: '',
      description: '',
      amount: '',
      date: '',
      attachments: [],
    },
    validationSchema: Yup.object({
      policyId: Yup.string().required('Policy is required'),
      type: Yup.string().required('Claim type is required'),
      description: Yup.string().required('Description is required'),
      amount: Yup.number().required('Amount is required'),
      date: Yup.date().required('Date is required'),
    }),
    onSubmit: async (values) => {
      try {
        await axios.post('/api/claims', values);
        setNewClaimDialogOpen(false);
        formik.resetForm();
        fetchClaims();
      } catch (err) {
        setError('Failed to submit claim');
        console.error(err);
      }
    },
  });

  const handleAddComment = async () => {
    try {
      await axios.post(`/api/claims/${selectedClaim.id}/comments`, {
        comment,
      });
      setCommentDialogOpen(false);
      setComment('');
      fetchClaims();
    } catch (err) {
      setError('Failed to add comment');
      console.error(err);
    }
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'approved':
        return 'success';
      case 'rejected':
        return 'error';
      case 'pending':
        return 'warning';
      default:
        return 'default';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  if (loading) {
    return <Typography>Loading...</Typography>;
  }

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4">My Claims</Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => setNewClaimDialogOpen(true)}
        >
          New Claim
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {claims.map((claim) => (
          <Grid item xs={12} md={6} lg={4} key={claim.id}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
                  <Typography variant="h6">{claim.type}</Typography>
                  <Chip
                    label={claim.status}
                    color={getStatusColor(claim.status)}
                    size="small"
                  />
                </Box>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Claim Number: {claim.claimNumber}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Date: {formatDate(claim.date)}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Amount: ${claim.amount}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Description: {claim.description}
                </Typography>
              </CardContent>
              <CardActions>
                <Tooltip title="View Details">
                  <IconButton
                    size="small"
                    onClick={() => {
                      setSelectedClaim(claim);
                      setDetailsDialogOpen(true);
                    }}
                  >
                    <DescriptionIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Add Comment">
                  <IconButton
                    size="small"
                    onClick={() => {
                      setSelectedClaim(claim);
                      setCommentDialogOpen(true);
                    }}
                  >
                    <CommentIcon />
                  </IconButton>
                </Tooltip>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* New Claim Dialog */}
      <Dialog
        open={newClaimDialogOpen}
        onClose={() => setNewClaimDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>New Claim</DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={formik.handleSubmit} sx={{ pt: 2 }}>
            <TextField
              fullWidth
              select
              label="Policy"
              name="policyId"
              value={formik.values.policyId}
              onChange={formik.handleChange}
              error={formik.touched.policyId && Boolean(formik.errors.policyId)}
              helperText={formik.touched.policyId && formik.errors.policyId}
              margin="normal"
            >
              {/* Add policy options here */}
            </TextField>
            <TextField
              fullWidth
              label="Claim Type"
              name="type"
              value={formik.values.type}
              onChange={formik.handleChange}
              error={formik.touched.type && Boolean(formik.errors.type)}
              helperText={formik.touched.type && formik.errors.type}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Description"
              name="description"
              value={formik.values.description}
              onChange={formik.handleChange}
              error={formik.touched.description && Boolean(formik.errors.description)}
              helperText={formik.touched.description && formik.errors.description}
              multiline
              rows={4}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Amount"
              name="amount"
              type="number"
              value={formik.values.amount}
              onChange={formik.handleChange}
              error={formik.touched.amount && Boolean(formik.errors.amount)}
              helperText={formik.touched.amount && formik.errors.amount}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Date"
              name="date"
              type="date"
              value={formik.values.date}
              onChange={formik.handleChange}
              error={formik.touched.date && Boolean(formik.errors.date)}
              helperText={formik.touched.date && formik.errors.date}
              InputLabelProps={{ shrink: true }}
              margin="normal"
            />
            <Button
              component="label"
              startIcon={<AttachmentIcon />}
              sx={{ mt: 2 }}
            >
              Upload Attachments
              <input type="file" hidden multiple />
            </Button>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setNewClaimDialogOpen(false)}>Cancel</Button>
          <Button onClick={formik.handleSubmit} variant="contained">
            Submit Claim
          </Button>
        </DialogActions>
      </Dialog>

      {/* Claim Details Dialog */}
      <Dialog
        open={detailsDialogOpen}
        onClose={() => setDetailsDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>Claim Details</DialogTitle>
        <DialogContent>
          {selectedClaim && (
            <Box sx={{ pt: 2 }}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <Typography variant="h6">Claim Information</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Claim Number: {selectedClaim.claimNumber}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Type: {selectedClaim.type}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Status: {selectedClaim.status}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Date: {formatDate(selectedClaim.date)}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Amount: ${selectedClaim.amount}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Description: {selectedClaim.description}
                  </Typography>
                </Grid>
                <Grid item xs={12}>
                  <Typography variant="h6" sx={{ mt: 2 }}>
                    Comments
                  </Typography>
                  <List>
                    {selectedClaim.comments?.map((comment, index) => (
                      <React.Fragment key={index}>
                        <ListItem>
                          <ListItemIcon>
                            <CommentIcon />
                          </ListItemIcon>
                          <ListItemText
                            primary={comment.text}
                            secondary={`${comment.author} - ${formatDate(
                              comment.date
                            )}`}
                          />
                        </ListItem>
                        {index < selectedClaim.comments.length - 1 && <Divider />}
                      </React.Fragment>
                    ))}
                  </List>
                </Grid>
              </Grid>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDetailsDialogOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>

      {/* Add Comment Dialog */}
      <Dialog open={commentDialogOpen} onClose={() => setCommentDialogOpen(false)}>
        <DialogTitle>Add Comment</DialogTitle>
        <DialogContent>
          <TextField
            label="Comment"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            fullWidth
            multiline
            rows={4}
            margin="normal"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCommentDialogOpen(false)}>Cancel</Button>
          <Button
            onClick={handleAddComment}
            variant="contained"
            disabled={!comment.trim()}
          >
            Add Comment
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Claims;
